sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//Determines if change was made in form for Field 'Request type'
   if (newValue === '') {
      return;
   }
	//Saves values from Field 'What needed' to variable
	var whatNeeded = g_form.getValue('u_what_needed');

   //Clear all of the choices from 'What needed' choice list
	g_form.clearOptions('u_what_needed');

	//If the value of 'Request type' is 'hr', add
	//two 'hr' choices and other to 'What needed' choice list
	if(newValue == 'hr'){
		g_form.addOption('u_what_needed','hr1','Human Resources 1');
		g_form.addOption('u_what_needed','hr2','Human Resources 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	//If the value of 'Request type' is 'facilities', add
	//two 'facilitices' choices and other to the 'What needed' choice list
	if(newValue == 'facilities'){
		g_form.addOption('u_what_needed','facilities1','Facilites 1');
		g_form.addOption('u_what_needed','facilities2','Facilites 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	//If the value of 'Request type' is 'legal', add
	//two 'legal' choices and other to 'What needed' choice list
	if(newValue == 'legal'){
		g_form.addOption('u_what_needed','legal1','Legal 1');
		g_form.addOption('u_what_needed','legal2','Legal 2');
		g_form.addOption('u_what_needed','other','Other');
	}
	//If form is loading and it is not a new record, set the 
	//'u_what_needed' value to the value before the record was loaded
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed',whatNeeded);
	}
}
